      gvSTEP := 'UPDATE PROCESS_LOG.BILL_SEQ='||TO_CHAR(gnBILL_SEQ)||':';
      UPDATE FY_TB_BL_BILL_PROCESS_LOG BL SET END_TIME=SYSDATE,
