            END IF;
         END IF;   
         PO_RC_AMT := NU_CHRG_AMT;
      END IF;  --2020/06/30      
      
      --CALL INSERT BILL_CI
      gvSTEP := 'INS_CI:';
            DBMS_OUTPUT.Put_Line('<ACCT_ID='||gnACCT_ID||', SUB_ID='||to_char(gnSUBSCR_ID)||', CHARGE_CODE='||TO_CHAR(gvCHARGE_CODE)||', CHRG_AMT='||TO_CHAR(NU_CHRG_AMT)||'>'); 
